import { useEffect } from 'react';
import useReveal from '../hooks/useReveal';

export default function Reservations(){

  const heading = useReveal('slideUp');
  const body = useReveal('fade');

  useEffect(()=>{
    const script=document.createElement('script');
    script.src='https://widgets.resy.com/embed.js';
    script.async=true;
    script.onload=()=>{
      if(window.resyWidget){
        window.resyWidget.addButton(
          document.getElementById('resyButton'),
          {venueId:9985, apiKey:'okbCVZshqhoQQYn7zT68y7aDSxhvLFdP', replace:true}
        );
      }
    };
    document.body.appendChild(script);
    return ()=>document.body.removeChild(script);
  },[]);

  return(
    <div className='min-h-screen bg-black text-white flex flex-col items-center justify-center p-6'>
      <h1 ref={heading} className='text-5xl font-serif text-gold mb-6 opacity-0'>Reservations</h1>
      <p ref={body} className='text-gray-300 max-w-xl text-center mb-10 opacity-0'>
        Secure your table at Carmelloâ€™s and enjoy an unforgettable evening of refined Italian dining.
      </p>
      <a
        id='resyButton'
        href='https://resy.com/cities/charlottesville-va/venues/carmellos-restaurant-wine-bar'
        className='bg-gold text-black px-10 py-4 rounded-lg text-xl font-semibold hover:bg-yellow-500 transition shadow-lg'
      >
        Book Now
      </a>
    </div>
  );
}
