import { useState } from "react";
import emailjs from "@emailjs/browser";
import useReveal from "../hooks/useReveal";

export default function Contact() {
  const fadeRef = useReveal("fade");
  const slideRef = useReveal("slideUp");

  const [formData, setFormData] = useState({
    from_name: "",
    from_email: "",
    phone: "",
    message: "",
  });

  const [status, setStatus] = useState("");

  function handleChange(e) {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  }

  function sendEmail(e) {
    e.preventDefault();

    emailjs
      .send(
        "service_orjwojm",      // SERVICE ID
        "template_9oxce3q",     // TEMPLATE ID
        formData,
        "DlhTOjdE7Pqq5UJHA"     // PUBLIC KEY
      )
      .then(
        () => {
          setStatus("SUCCESS");
          setFormData({
            from_name: "",
            from_email: "",
            phone: "",
            message: "",
          });
        },
        () => {
          setStatus("ERROR");
        }
      );
  }

  return (
    <div className="bg-black text-white min-h-screen pt-24 px-6">
      <h1 ref={fadeRef} className="text-5xl font-serif text-gold mb-10 text-center opacity-0">
        Contact Us
      </h1>

      <form
        ref={slideRef}
        onSubmit={sendEmail}
        className="max-w-xl mx-auto space-y-6 opacity-0"
      >
        <div>
          <label className="block text-gold mb-1 font-serif">Name</label>
          <input
            type="text"
            name="from_name"
            value={formData.from_name}
            onChange={handleChange}
            required
            className="w-full p-3 rounded bg-black border border-gold focus:outline-none focus:border-yellow-500"
          />
        </div>

        <div>
          <label className="block text-gold mb-1 font-serif">Email</label>
          <input
            type="email"
            name="from_email"
            value={formData.from_email}
            onChange={handleChange}
            required
            className="w-full p-3 rounded bg-black border border-gold focus:outline-none focus:border-yellow-500"
          />
        </div>

        <div>
          <label className="block text-gold mb-1 font-serif">Phone</label>
          <input
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            className="w-full p-3 rounded bg-black border border-gold focus:outline-none focus:border-yellow-500"
          />
        </div>

        <div>
          <label className="block text-gold mb-1 font-serif">Message</label>
          <textarea
            name="message"
            value={formData.message}
            onChange={handleChange}
            rows="5"
            required
            className="w-full p-3 rounded bg-black border border-gold focus:outline-none focus:border-yellow-500"
          />
        </div>

        <button
          type="submit"
          className="w-full bg-gold text-black py-3 text-xl font-semibold rounded hover:bg-yellow-500 transition"
        >
          Send Message
        </button>

        {status === "SUCCESS" && (
          <p className="text-green-400 text-center mt-2">Message sent successfully!</p>
        )}
        {status === "ERROR" && (
          <p className="text-red-400 text-center mt-2">Something went wrong. Please try again.</p>
        )}
      </form>
    </div>
  );
}
