import useReveal from '../hooks/useReveal';

export default function Home(){

  const heroText = useReveal('slideUp');
  const about = useReveal('fade');
  const wine = useReveal('slideUp');
  const dining = useReveal('fade');

  return (
    <div className="bg-black text-white">

      {/* HERO */}
      <div className="relative h-[80vh]">
        <img src="/images/hero.png" className="w-full h-full object-cover opacity-60"/>
        <div className="absolute inset-0 bg-black/50 flex items-center justify-center">
          <h1 ref={heroText} className="text-6xl font-serif text-gold opacity-0">Carmello’s Restaurant & Wine Bar</h1>
        </div>
      </div>

      {/* ABOUT */}
      <section ref={about} className="container mx-auto px-6 py-20 opacity-0">
        <h2 className="text-4xl font-serif text-gold mb-6">About Carmello’s</h2>
        <p className="text-gray-300 max-w-3xl text-lg leading-relaxed">
          Since opening, Carmello’s has set the standard for elegant Italian dining in Charlottesville.
          Our chefs blend time-honored Roman tradition with contemporary refinement, crafting dishes that
          capture the heart of Italy with a luxurious, modern touch.
        </p>
      </section>

      {/* WINE */}
      <section ref={wine} className="container mx-auto px-6 py-20 opacity-0 text-center">
        <h2 className="text-4xl font-serif text-gold mb-6">Fine Italian Wines</h2>
        <img src="/images/wine.jpg" className="mx-auto w-1/2 rounded-lg shadow-2xl"/>
      </section>

      {/* DINING */}
      <section ref={dining} className="container mx-auto px-6 py-20 opacity-0 text-center">
        <h2 className="text-4xl font-serif text-gold mb-6">Our Dining Room</h2>
        <img src="/images/dining.jpg" className="mx-auto w-3/4 rounded-lg shadow-2xl"/>
      </section>

    </div>
  );
}
